const initialState = {
    isFetching:false,
    caracthers:[],
    errorMessage:undefined,
};

export function fetchCarachtersReducer(state = initialState,action){
    switch(action.type){
        case 'FETCH_CARACHTERS_START':
        case 'FILTER_CARACHTERS_START':
            return {
                ...state,
                isFetching:true,
            };
        case 'FETCH_CARACHTERS_SUCCESS':
            return {
                ...state,
                errorMessage:undefined,
                isFetching:false,
                products:action.payload,
            }
        case 'FETCH_CARACHTERS_ERROR':
            return {
                ...state,
                caracthers:[],
                isFetching:false,
                errorMessage:action.payload,
            }
        default:
            return state;   
    }
}
