import "./filtros.css";
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import { fetchCharactersStart, filterCharactersStart } from '../../actions/index';
import { useState } from "react";


const Filtros = () => {
  const [searchText, setSearchText] = useState("");

  function changeSearchText(event) {
    setSearchText(event.target.value);
  }

  function filterCharacters() {
    if (!searchText) {
      fetchCharactersStart();
    } else {
      filterCharactersStart(searchText);
    }
  }
  return (
  <>
  
    <div className="filtros">
      <label for="nome">Filtrar por nome:</label>
      <input
        type="text"
        value={searchText}
        placeholder="Rick, Morty, Beth, Alien, ...etc"
        name="nome"
        onChange={changeSearchText}
      />
    </div>
     <button type="button" onClick={filterCharacters} className={"primary"} sx={{ display: 'flex', alignItems:'center',justifyContent:'center' }}>Filtrar</button>
     </>
  );
};

export default Filtros;
