type Character = {
  id:number,
  name:string,
  image:string;
}

export function fetchCharactersStart() {
  return {
    type: 'FETCH_CHARACTERS_START',
  };
}

export function filterCharactersStart(searchText:string) {
    return {
      type: 'FILTER_CHARACTERS_START',
      payload: searchText,
    };
  }

  export function fetchCharactersSuccess(characters:Character[]) {
    return {
      type: 'FETCH_CHARACTERS_SUCCESS',
      payload: characters,
    };
  }

  export function fetchCharactersError(characters:Character[]) {
    return {
      type: 'FETCH_CHARACTERS_ERROR',
      payload:characters,
    };
  }


 export function paginateCharactersForward(page: number) {
    return {
      type: "PAGINATE_CHARACTERS_FORWARD",
      payload: page,
    };
  }
  export function paginateCharactersBackwards(url: string) {
    return {
      type: "PAGINATE_CHARACTERS_BACKWARDS",
      payload: url,
    };
  }
