import BotaoFavorito from "../botoes/botao-favorito.componente";
import "./card-personagem.css";
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import { fetchCharactersStart, filterCharactersStart } from '../../actions';
import { useEffect } from "react";

/**
 * Card para cada personagem dentro da grade de personagem.
 *
 * Você precisará adicionar as propriedades necessárias para exibir os dados dos personagens
 *
 *
 * @returns Elemento JSX
 */
const CardPersonagem = ({ characters, filterCharactersStart, fetchCharactersStart }) => {
  useEffect(() => fetchCharactersStart(), [fetchCharactersStart]);
  return (
    <>
      {/* {
        characters.isFetching ? (
          <span>Carregando...</span>
        ) : ( */}
          <>
            <div className="card-personagem">
              <img
                src="https://rickandmortyapi.com/api/character/avatar/1.jpeg"
                alt="Rick Sanchez"
              />
              <div className="card-personagem-body">
                <span>Rick Sanchez</span>
                <BotaoFavorito isFavorito={false} />
              </div>
            </div>
            {/* <ul>{
              characters.characters.map(character => (
                <li className="card-personagem" key={character.id}>
                <p> {character.name}</p>
                 <BotaoFavorito  />
                 <img src={character.image} alt={character.name} />
               </li>
              ))
            }
            </ul> */}
          </>
        {/* )} */}

    </>
  );
}

const mapStateToProps = (state) => ({
  characters: state.characters,
})


const mapDispatchToProps = dispatch => bindActionCreators({
  fetchCharactersStart, filterCharactersStart,
}, dispatch);

export default connect()(CardPersonagem);
