import {takeLatest,call,put} from 'redux-saga/effects';
import { fetchCarachtersSuccess,fetchCarachtersError } from '../actions';

function* fetchCarachtersSaga(){
    try{
        const response = yield call(fetch,'https://rickandmortyapi.com/api/character/');
        const data = yield response.json();
        yield put(fetchCarachtersSuccess(data));
    }catch(error){
       yield put(fetchCarachtersError(error.message));
    }
}
