import "./paginacao.css";
import { useState } from "react";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import {
  paginateCharactersForward,
  paginateCharactersBackwards,
} from "../../store/actions/characters.actions";


/**
 * Componente que contém os botões para paginar
 *
 * Você deve adicionar as propriedades necessárias para que funcione corretamente
 *
 *
 * @returns Elemento JSX
 */
const Paginacao = () => {
  const [currentPage, setCurrentPage] = useState(1);

  function changeNextPage() {
    setCurrentPage(currentPage + 1);
    paginateCharactersForward(currentPage)
  }

  function changePreviousPage() {
    setCurrentPage(currentPage - 1);
    paginateCharactersBackwards(currentPage);

  }


  return (
    <div className="paginacao">
      <button onClick={changeNextPage} disabled={false} className={"primary"}>
        Anterior
      </button>
      <button onClick={changePreviousPage} className={"primary"}>
        Próximo
      </button>
    </div>
  );
};

const mapStateToProps = (state) => ({
  characters: state.characters,
})


const mapDispatchToProps = dispatch => bindActionCreators({
  fetchCharactersStart, filterCharactersStart,
}, dispatch);


export default connect(mapStateToProps,mapDispatchToProps)(Paginacao);
